@Override
public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {

        for (int j = oldVersion; j <= newVersion; j++) {
            switch (j) {
            case 2:
//
                db.execSQL(TEMP_SQL_CREATE_TABLE_SUBSCRIBE);
 //OnCreate
