Cursor cursor = db.rawQuery(INSERT_SUBSCRIBE, null);
                if (cursor.moveToFirst()) {
                    do {
                        db.execSQL(cursor.getString(cursor
                                .getColumnIndex("insertSQL")));
                    } while (cursor.moveToNext());
                }

                cursor.close();
//
                db.execSQL(DROP_TEMP_SUBSCRIBE);

                break;

            default:
                break;
            }
        }
}
