    private static SimpleDateFormat getSdf(final String pattern) {
        ThreadLocal<SimpleDateFormat> tl = sdfMap.get(pattern);

        // sdfMapputsdf
        if (tl == null) {
            synchronized (lockObj) {
                tl = sdfMap.get(pattern);
